import React, { useState } from 'react';
import axios from 'axios';
import '../styles/WeatherApp.css'
import Cards from './Cards';


function WeatherApp() {

    const [weatherData, setWeatherData] = useState([]);
    const [country, setCountry] = useState('');
    const [error, setError] = useState('');

    const fetchWeatherData = async () => {
        const apiKey = 'aa56feb06eed8a2a227df36ccf5b528e';
        try {
            const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${country}&units=metric&appid=${apiKey}`);
            setWeatherData([response.data]);
            console.log(response.data)
            setError('');
        } catch (err) {
            setError('City not found!');
            setWeatherData([]);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        fetchWeatherData();
    };

  return (
    <div className='container mt-5 mb-5 row p-0'>
        <div className="col-md-4 section1 p-4">
                <form className="search input-group" onSubmit={handleSubmit}>
                    <input 
                        type="text" 
                        placeholder="Enter city name" 
                        className="form-control border-end-0 border rounded-pill" 
                        value={country}
                        onChange={(e) => setCountry(e.target.value)}
                    />
                    <button type='submit' id="searchButton" className="rounded-pill border ms-3">Search</button>
                </form>
                <div>
                    <h4 className="text-center mt-5">Provide you a world wide weather forecast</h4>
                    <hr />
                    <div id="weather" className="weather">
                        <p>{error}</p>
                        
                    </div>
                </div>
        </div>
        <div className="col-md-8 section2 p-4">
            <h5 class="text-start ms-3">Check out today's weather information</h5>
            <div class="row mt-4" id="weather-cards">
                <div class="col-sm-4 mt-3">
                    <div class="card">
                    {weatherData && (
                <div style={styles.cardsContainer}>
                    <Cards title="Temperature" value={weatherData.main.temp} unit="°C" />
                    <Cards title="Feels Like" value={weatherData.main.feels_like} unit="°C" />
                    <Cards title="Minimum Temperature" value={weatherData.main.temp_min} unit="°C" />
                    <Cards title="Maximum Temperature" value={weatherData.main.temp_max} unit="°C" />
                    <Cards title="Humidity" value={weatherData.main.humidity} unit="%" />
                    <Cards title="Pressure" value={weatherData.main.pressure} unit="hPa" />
                    <Card title="Wind Speed" value={weatherData.wind.speed} unit="m/s" />
                    <Card title="Wind Direction" value={weatherData.wind.deg} unit="°" />
                    <Card title="Condition" value={weatherData.weather[0].description} unit="" />
                </div>
            )}
                    </div>
                </div>
            </div>
            
        </div>
    </div>
  )
}

export default WeatherApp
