import React from 'react';
import '../styles/WeatherApp.css'

function WeatherApp() {

    const [weatherData, setWeatherData] = useState([]);
    const [country, setCountry] = useState('');
    const [error, setError] = useState('');

    const fetchWeatherData = async () => {
        const apiKey = 'YOUR_API_KEY';
        try {
            const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${country}&units=metric&appid=${apiKey}`);
            setWeatherData([response.data]);
            setError('');
        } catch (err) {
            setError('Unable to fetch weather data. Please check the country name and try again.');
            setWeatherData([]);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        fetchWeatherData();
    };

  return (
    <div className='container mt-5 mb-5 row p-0'>
        <div class="col-md-4 section1 p-4">
            <form>
                <input
                    type="text"
                    value={country}
                    onChange={(e) => setCountry(e.target.value)}
                    placeholder="Enter country name"
                />
                <button type="submit" style={styles.button}>Search</button>
            </form>
        </div>
        <div class="col-md-8 section1 p-4">

        </div>
    </div>
  )
}

export default WeatherApp
