import React, { useState } from 'react';
import axios from 'axios';
import '../styles/WeatherApp.css'
import Cards from '../components/Cards';


function WeatherApp() {

    const [weatherData, setWeatherData] = useState({});
    const [isWeatherData, setIsWeatherData] = useState(false);
    const [country, setCountry] = useState('');
    const [error, setError] = useState('');
    const { main, name, sys, weather, wind, coord, timezone } = weatherData;

    const getIconUrl = (iconCode) => {
        return iconCode ? `./animated/${iconCode}.svg` : '';
    };

    

    const fetchWeatherData = async () => {
        const apiKey = 'aa56feb06eed8a2a227df36ccf5b528e';
        try {
            const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${country}&units=metric&appid=${apiKey}`);
            setWeatherData(response.data);  
            setIsWeatherData(true);  
            console.log(response.data)     
            setError('');
        } catch (err) {
            setError('City not found!');
            setWeatherData({});
            setIsWeatherData(false);
        }       
    };
    

    const handleSubmit = (e) => {
        e.preventDefault();
        fetchWeatherData();
    };

  return (
    <div className='container mt-5 mb-5 row p-0'>
        <div className="col-md-4 section1 p-4">
                <form className="search input-group" onSubmit={handleSubmit}>
                    <input 
                        type="text" 
                        placeholder="Enter city name" 
                        className="form-control border-end-0 border rounded-pill" 
                        value={country}
                        onChange={(e) => setCountry(e.target.value)}
                    />
                    <button type='submit' id="searchButton" className="rounded-pill border ms-3">Search</button>
                </form>
                <div>
                    <h4 className="text-center mt-5">Provide you a world wide weather forecast</h4>
                    <hr />
                    <div id="weather" className="weather">
                        <p>{error}</p>
                        <h4 className='text-capitalize'>{country}, {isWeatherData && sys.country}</h4>
                        <img  className="mt-1" />
                        <h4>{weather[0].description}</h4>
                        <p>{main.temp} °C</p>
                    </div>
                    {/* <button onClick={() => console.log(weatherData)}>Button</button> */}
                </div>
        </div>
        <div className="col-md-8 section2 p-4">
            <h5 className="text-start ms-3">Check out today's weather information</h5>
            <div className="row mt-4">              
                <div className="col-sm-4 mt-3">
                    <div className="card">
                    {isWeatherData ? <Cards title="Wind" value={wind.speed}/> : <Cards title="Wind" />}            
                    </div>  
                </div>
                <div className="col-sm-4 mt-3">
                    <div className="card">
                    {isWeatherData ? <Cards title="Humidity" value={main.humidity}/> :  <Cards title="Humidity" />}
                    </div>
                </div>
                <div className="col-sm-4 mt-3">
                    <div className="card">
                    {isWeatherData ? <Cards title="Real Feel" value={main.temp}/> : <Cards title="Real Feel" />}
                    </div>
                </div>
                <div className="col-sm-4 mt-3">
                    <div className="card">
                    {isWeatherData ? <Cards title="Sea Level" value={main.sea_level}/> : <Cards title="Sea Level"/>}
                    </div>
                </div>
                <div className="col-sm-4 mt-3">
                    <div className="card">
                    {isWeatherData ? <Cards title="Pressure" value={main.pressure}/> : <Cards title="Pressure"/>}
                    </div>
                </div>
                <div className="col-sm-4 mt-3">
                    <div className="card">
                    {isWeatherData ? <Cards title="lat & lon" value={coord.lat}/> : <Cards title="lat & lon"/>}
                    </div>
                </div>
                <div className="col-sm-4 mt-3">
                    <div className="card">
                    {isWeatherData ? <Cards title="Sun Rise Time" value={sys.sunrise}/> : <Cards title="Sun Rise Time"/>}
                    </div>
                </div>
                <div className="col-sm-4 mt-3">
                    <div className="card">
                    {isWeatherData ? <Cards title="Sun Set Time" value={sys.sunset}/> : <Cards title="Sun Set Time"/>}
                    </div>
                </div>
                <div className="col-sm-4 mt-3">
                    <div className="card">
                    {isWeatherData ? <Cards title="Time Zone" value={timezone}/> : <Cards title="Time Zone"/>}
                    </div>
                </div>
            </div>
        </div>
    </div>
  )
}

export default WeatherApp
